import React, { useState, useRef, useEffect } from 'react';

const DEMO_TRACKS = {
  '–¢—Ä–µ–∫ 1': 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3',
  '–¢—Ä–µ–∫ 2': 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-2.mp3',
  '–¢—Ä–µ–∫ 3': 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-3.mp3',
  '–ü–æ–ø—É–ª—è—Ä–Ω—ã–π —Ç—Ä–µ–∫': 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-4.mp3',
  '–•–∏—Ç –¥–Ω—è': 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-5.mp3',
  '–ù–æ–≤–∏–Ω–∫–∞': 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-6.mp3',
  '–õ—é–±–∏–º–∞—è –ø–µ—Å–Ω—è': 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-7.mp3',
  '–†–µ—Ç—Ä–æ —Ö–∏—Ç': 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-8.mp3',
  '–ò–Ω–¥–∏ —Ç—Ä–µ–∫': 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-9.mp3'
};

function MusicPlayer({ currentTrack, onTrackChange }) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const audioRef = useRef(null);

  useEffect(() => {
    // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–π —Ç—Ä–µ–∫ –ø—Ä–∏ —Å–º–µ–Ω–µ
    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current.ontimeupdate = null;
        audioRef.current.onloadedmetadata = null;
        audioRef.current.onended = null;
        audioRef.current.onerror = null;
        audioRef.current = null;
      }
    };
  }, [currentTrack]);

  const playRealAudio = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current = null;
    }

    const audioUrl = DEMO_TRACKS[currentTrack.track];
    if (audioUrl) {
      audioRef.current = new Audio(audioUrl);
      audioRef.current.volume = 0.5;
      audioRef.current.crossOrigin = 'anonymous';

      audioRef.current.onloadedmetadata = () => {
        if (audioRef.current) {
          setDuration(Math.floor(audioRef.current.duration));
        }
      };

      audioRef.current.ontimeupdate = () => {
        if (audioRef.current) {
          setCurrentTime(Math.floor(audioRef.current.currentTime));
        }
      };

      audioRef.current.onended = () => {
        setIsPlaying(false);
        setCurrentTime(0);
      };

      audioRef.current.onerror = () => {
        console.log('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ç—Ä–µ–∫–∞');
        setIsPlaying(false);
      };

      audioRef.current.play().catch(e => {
        console.log('–û—à–∏–±–∫–∞ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è:', e);
        setIsPlaying(false);
      });
    }
  };

  const togglePlayback = () => {
    if (!currentTrack) return;

    if (!isPlaying) {
      setIsPlaying(true);
      playRealAudio();
    } else {
      setIsPlaying(false);
      if (audioRef.current) {
        audioRef.current.pause();
      }
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (!currentTrack) {
    return (
      <div className="player-container">
        <div style={{ textAlign: 'center', color: '#666' }}>
          –í—ã–±–µ—Ä–∏—Ç–µ —Ç—Ä–µ–∫ –¥–ª—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
        </div>
      </div>
    );
  }

  return (
    <div className="player-container">
      <h3 style={{ marginBottom: '15px' }}>–°–µ–π—á–∞—Å –∏–≥—Ä–∞–µ—Ç</h3>
      <div className="current-track">
        <div className="track-cover"></div>
        <div className="track-info">
          <div style={{ fontWeight: 'bold' }}>{currentTrack.track}</div>
          <div style={{ color: '#666', fontSize: '14px' }}>{currentTrack.playlist}</div>
        </div>
        <button className="play-btn" onClick={togglePlayback}>
          {isPlaying ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
        </button>
      </div>

      {/* –ü—Ä–æ–≥—Ä–µ—Å—Å –±–∞—Ä */}
      <div style={{ marginTop: '15px' }}>
        <input
          type="range"
          min="0"
          max={duration || 0}
          value={currentTime}
          onChange={(e) => {
            const newTime = parseInt(e.target.value);
            setCurrentTime(newTime);
            if (audioRef.current) {
              audioRef.current.currentTime = newTime;
            }
          }}
          style={{
            width: '100%',
            height: '4px',
            background: '#ddd',
            borderRadius: '2px',
            marginBottom: '5px',
            cursor: 'pointer'
          }}
        />
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          fontSize: '12px', 
          color: '#666' 
        }}>
          <span>{formatTime(currentTime)}</span>
          <span>{formatTime(duration)}</span>
        </div>
      </div>

      {isPlaying && (
        <div style={{ 
          textAlign: 'center', 
          marginTop: '10px', 
          fontSize: '12px', 
          color: '#FF6B35' 
        }}>
          üéµ –í–æ—Å–ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç—Å—è –¥–µ–º–æ-–≤–µ—Ä—Å–∏—è
        </div>
      )}
    </div>
  );
}

export default MusicPlayer;
