    // –ò–≥—Ä–∞–µ–º –º–µ–ª–æ–¥–∏—é
    frequencies.forEach((freq, i) => {
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();

      oscillator.frequency.setValueAtTime(freq, audioContext.currentTime + i * 0.5);
      oscillator.type = 'sine';
      gainNode.gain.setValueAtTime(0.2, audioContext.currentTime + i * 0.5);
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + i * 0.5 + 0.4);

      oscillator.connect(gainNode);
      gainNode.connect(audioContext.destination);

      oscillator.start(audioContext.currentTime + i * 0.5);
      oscillator.stop(audioContext.currentTime + i * 0.5 + 0.4);
    });

    setDuration(180);
    const interval = setInterval(() => {
      setCurrentTime(prev => {
        if (prev >= 180) {
          setIsPlaying(false);
          clearInterval(interval);
          return 0;
        }
        return prev + 1;
      });
    }, 1000);
  };

  const togglePlayback = () => {
    if (!currentTrack) return;

    if (!isPlaying) {
      setIsPlaying(true);
      playRealAudio();
    } else {
      setIsPlaying(false);
      if (audioRef.current) {
        audioRef.current.pause();
      }
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (!currentTrack) {
    return (
      <div className="player-container">
        <div style={{ textAlign: 'center', color: '#666' }}>
          –í—ã–±–µ—Ä–∏—Ç–µ —Ç—Ä–µ–∫ –¥–ª—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
        </div>
      </div>
    );
  }

  return (
    <div className="player-container">
      <h3 style={{ marginBottom: '15px' }}>–°–µ–π—á–∞—Å –∏–≥—Ä–∞–µ—Ç</h3>
      <div className="current-track">
        <div className="track-cover"></div>
        <div className="track-info">
          <div style={{ fontWeight: 'bold' }}>{currentTrack.track}</div>
          <div style={{ color: '#666', fontSize: '14px' }}>{currentTrack.playlist}</div>
        </div>
        <button className="play-btn" onClick={togglePlayback}>
          {isPlaying ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
        </button>
      </div>

      {/* –ü—Ä–æ–≥—Ä–µ—Å—Å –±–∞—Ä */}
      <div style={{ marginTop: '15px' }}>
        <input
          type="range"
          min="0"
          max={duration || 0}
          value={currentTime}
          onChange={(e) => {
            const newTime = parseInt(e.target.value);
            setCurrentTime(newTime);
            if (audioRef.current) {
              audioRef.current.currentTime = newTime;
            }
          }}
          style={{
            width: '100%',
            height: '4px',
            background: '#ddd',
            borderRadius: '2px',
            marginBottom: '5px',
            cursor: 'pointer'
          }}
        />
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          fontSize: '12px', 
          color: '#666' 
        }}>
          <span>{formatTime(currentTime)}</span>
          <span>{formatTime(duration)}</span>
        </div>
      </div>

      {isPlaying && (
        <div style={{ 
          textAlign: 'center', 
          marginTop: '10px', 
          fontSize: '12px', 
          color: '#FF6B35' 
        }}>
          üéµ –ò–≥—Ä–∞–µ—Ç: {currentTrack.track}
        </div>
      )}
    </div>
  );
}

export default MusicPlayer;
