import React, { useState, useRef, useEffect } from 'react';

// –ë–µ—Å–ø–ª–∞—Ç–Ω—ã–µ —Ç—Ä–µ–∫–∏ —Å Pixabay Music
const DEMO_TRACKS = {
  '–°–µ—Ä–≥–µ–π –õ–∞–∑–∞—Ä–µ–≤ - –ú—É–∑—ã–∫–∞ –Ω–∞—Å': 'https://pixabay.com/music/upbeat-happy-rock-113390/',
  '–î–∏–º–∞ –ë–∏–ª–∞–Ω - –ù–µ –º–æ–ª—á–∏': 'https://pixabay.com/music/upbeat-good-night-160166/',
  '–°–µ—Ä–≥–µ–π –õ–∞–∑–∞—Ä–µ–≤ - –°–¥–∞–≤–∞–π—Å—è': 'https://pixabay.com/music/upbeat-energetic-rock-distorted-guitar-drums-bass-132948/',
  '–î–∏–º–∞ –ë–∏–ª–∞–Ω - –ù–∞ –±–µ—Ä–µ–≥—É –Ω–µ–±–∞': 'https://pixabay.com/music/ambient-deep-meditation-192828/',
  '–°–µ—Ä–≥–µ–π –õ–∞–∑–∞—Ä–µ–≤ - –ü—É—Å—Ç—å –≤–µ—Å—å –º–∏—Ä –ø–æ–¥–æ–∂–¥—ë—Ç': 'https://pixabay.com/music/ambient-relaxing-145038/',
  '–î–∏–º–∞ –ë–∏–ª–∞–Ω - –ü—Ä–æ—Å—Ç–æ –æ—Ç–ø—É—Å—Ç–∏': 'https://pixabay.com/music/ambient-meditation-142387/'
};

function MusicPlayer({ currentTrack, onTrackChange }) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const intervalRef = useRef(null);

  useEffect(() => {
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [currentTrack]);

  const playRealAudio = () => {
    // –ú–∞–ø–ø–∏–Ω–≥ —Ç—Ä–µ–∫–æ–≤ –Ω–∞ —Ñ–∞–π–ª—ã
    const trackFiles = {
      '–°–æ–ª–Ω–µ—á–Ω–∞—è –º–µ–ª–æ–¥–∏—è': '/music/happy1.mp3',
      '–†–∞–¥–æ—Å—Ç–Ω—ã–π —Ä–∏—Ç–º': '/music/happy2.mp3',
      '–ü–æ–∑–∏—Ç–∏–≤–Ω—ã–µ –≤–∏–±—Ä–∞—Ü–∏–∏': '/music/happy3.mp3',

      '–ú–µ–ª–∞–Ω—Ö–æ–ª–∏—á–Ω–∞—è –±–∞–ª–ª–∞–¥–∞': '/music/sad1.mp3',
      '–ì—Ä—É—Å—Ç–Ω–∞—è –º–µ–ª–æ–¥–∏—è': '/music/sad2.mp3',
      '–ù–æ—Å—Ç–∞–ª—å–≥–∏—á–µ—Å–∫–∏–µ –Ω–æ—Ç—ã': '/music/sad3.mp3',

      '–≠–Ω–µ—Ä–≥–∏—á–Ω—ã–π —Ä–æ–∫': '/music/angry1.mp3',
      '–ú–æ—â–Ω—ã–π –±–∏—Ç': '/music/angry2.mp3',
      '–ê–≥—Ä–µ—Å—Å–∏–≤–Ω—ã–µ —Ä–∏—Ñ—Ñ—ã': '/music/angry3.mp3',

      '–†–∞—Å—Å–ª–∞–±–ª—è—é—â–∞—è –º—É–∑—ã–∫–∞': '/music/calm1.mp3',
      '–ú–µ–¥–∏—Ç–∞—Ç–∏–≤–Ω–∞—è –∫–æ–º–ø–æ–∑–∏—Ü–∏—è': '/music/calm2.mp3',
      '–£–º–∏—Ä–æ—Ç–≤–æ—Ä—è—é—â–∏–µ –∑–≤—É–∫–∏': '/music/calm3.mp3',

      '–î—Ä–∞–π–≤–æ–≤—ã–π —Ç—Ä–µ–∫': '/music/excited1.mp3',
      '–¢–∞–Ω—Ü–µ–≤–∞–ª—å–Ω—ã–π —Ö–∏—Ç': '/music/excited2.mp3',
      '–≠–ª–µ–∫—Ç—Ä–æ–Ω–Ω—ã–µ –±–∏—Ç—ã': '/music/excited3.mp3',

      '–£—Å–ø–æ–∫–∞–∏–≤–∞—é—â–∞—è –º—É–∑—ã–∫–∞': '/music/anxious1.mp3',
      '–ú—è–≥–∫–∞—è –º–µ–ª–æ–¥–∏—è': '/music/anxious2.mp3',
      '–ê–Ω—Ç–∏—Å—Ç—Ä–µ—Å—Å —Ç—Ä–µ–∫': '/music/anxious3.mp3',

      '–†–æ–º–∞–Ω—Ç–∏—á–µ—Å–∫–∞—è –º–µ–ª–æ–¥–∏—è': '/music/love1.mp3',
      '–õ—é–±–æ–≤–Ω–∞—è –±–∞–ª–ª–∞–¥–∞': '/music/love2.mp3',
      '–ù–µ–∂–Ω—ã–µ –Ω–æ—Ç—ã': '/music/love3.mp3',

      '–°–ø–æ–∫–æ–π–Ω–∞—è –∫–æ–º–ø–æ–∑–∏—Ü–∏—è': '/music/tired1.mp3',
      '–†–∞—Å—Å–ª–∞–±–ª—è—é—â–∏–µ –∑–≤—É–∫–∏': '/music/tired2.mp3',
      '–ú–µ–¥–ª–µ–Ω–Ω–∞—è –º–µ–ª–æ–¥–∏—è': '/music/tired3.mp3'
    };

    const audioFile = trackFiles[currentTrack.track];
    if (!audioFile) return;

    if (intervalRef.current?.audio) {
      intervalRef.current.audio.pause();
    }

    const audio = new Audio(audioFile);
    audio.volume = 0.7;
    intervalRef.current = { audio };

    audio.onloadedmetadata = () => {
      setDuration(Math.floor(audio.duration));
    };

    audio.ontimeupdate = () => {
      setCurrentTime(Math.floor(audio.currentTime));
    };

    audio.onended = () => {
      setIsPlaying(false);
      setCurrentTime(0);
    };

    audio.play().catch(() => {
      setIsPlaying(false);
    });
  };

  const togglePlayback = () => {
    if (!currentTrack) return;

    if (!isPlaying) {
      setIsPlaying(true);
      setCurrentTime(0);
      playRealAudio();

      intervalRef.current = setInterval(() => {
        setCurrentTime(prev => {
          if (prev >= 180) {
            setIsPlaying(false);
            clearInterval(intervalRef.current);
            return 0;
          }
          return prev + 1;
        });
      }, 1000);
    } else {
      setIsPlaying(false);
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
      setCurrentTime(0);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (!currentTrack) {
    return (
      <div className="player-container">
        <div style={{ textAlign: 'center', color: '#666' }}>
          –í—ã–±–µ—Ä–∏—Ç–µ —Ç—Ä–µ–∫ –¥–ª—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
        </div>
      </div>
    );
  }

  return (
    <div className="player-container">
      <h3 style={{ marginBottom: '15px' }}>–°–µ–π—á–∞—Å –∏–≥—Ä–∞–µ—Ç</h3>
      <div className="current-track">
        <div className="track-cover"></div>
        <div className="track-info">
          <div style={{ fontWeight: 'bold' }}>{currentTrack.track}</div>
          <div style={{ color: '#666', fontSize: '14px' }}>{currentTrack.playlist}</div>
        </div>
        <button className="play-btn" onClick={togglePlayback}>
          {isPlaying ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
        </button>
      </div>

      {/* –ü—Ä–æ–≥—Ä–µ—Å—Å –±–∞—Ä */}
      <div style={{ marginTop: '15px' }}>
        <input
          type="range"
          min="0"
          max={duration || 0}
          value={currentTime}
          onChange={(e) => {
            const newTime = parseInt(e.target.value);
            setCurrentTime(newTime);
          }}
          style={{
            width: '100%',
            height: '4px',
            background: '#ddd',
            borderRadius: '2px',
            marginBottom: '5px',
            cursor: 'pointer'
          }}
        />
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          fontSize: '12px', 
          color: '#666' 
        }}>
          <span>{formatTime(currentTime)}</span>
          <span>{formatTime(duration)}</span>
        </div>
      </div>

      {isPlaying && (
        <div style={{ 
          textAlign: 'center', 
          marginTop: '10px', 
          fontSize: '12px', 
          color: '#FF6B35' 
        }}>
          üéµ –ò–≥—Ä–∞–µ—Ç: {currentTrack.track}
        </div>
      )}
    </div>
  );
}

export default MusicPlayer;
