import React, { useState, useRef, useEffect } from 'react';

// –ë–µ—Å–ø–ª–∞—Ç–Ω—ã–µ —Ç—Ä–µ–∫–∏ —Å Pixabay Music
const DEMO_TRACKS = {
  '–°–µ—Ä–≥–µ–π –õ–∞–∑–∞—Ä–µ–≤ - –ú—É–∑—ã–∫–∞ –Ω–∞—Å': 'https://pixabay.com/music/upbeat-happy-rock-113390/',
  '–î–∏–º–∞ –ë–∏–ª–∞–Ω - –ù–µ –º–æ–ª—á–∏': 'https://pixabay.com/music/upbeat-good-night-160166/',
  '–°–µ—Ä–≥–µ–π –õ–∞–∑–∞—Ä–µ–≤ - –°–¥–∞–≤–∞–π—Å—è': 'https://pixabay.com/music/upbeat-energetic-rock-distorted-guitar-drums-bass-132948/',
  '–î–∏–º–∞ –ë–∏–ª–∞–Ω - –ù–∞ –±–µ—Ä–µ–≥—É –Ω–µ–±–∞': 'https://pixabay.com/music/ambient-deep-meditation-192828/',
  '–°–µ—Ä–≥–µ–π –õ–∞–∑–∞—Ä–µ–≤ - –ü—É—Å—Ç—å –≤–µ—Å—å –º–∏—Ä –ø–æ–¥–æ–∂–¥—ë—Ç': 'https://pixabay.com/music/ambient-relaxing-145038/',
  '–î–∏–º–∞ –ë–∏–ª–∞–Ω - –ü—Ä–æ—Å—Ç–æ –æ—Ç–ø—É—Å—Ç–∏': 'https://pixabay.com/music/ambient-meditation-142387/'
};

function MusicPlayer({ currentTrack, onTrackChange }) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const intervalRef = useRef(null);

  useEffect(() => {
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [currentTrack]);

  const playRealAudio = async () => {
    const audioContext = new (window.AudioContext || window.webkitAudioContext)();

    if (audioContext.state === 'suspended') {
      await audioContext.resume();
    }

    const trackMelodies = {
      '–°–æ–ª–Ω–µ—á–Ω–∞—è –º–µ–ª–æ–¥–∏—è': [523, 659, 784, 659, 523],
      '–†–∞–¥–æ—Å—Ç–Ω—ã–π —Ä–∏—Ç–º': [440, 554, 698, 554, 440],
      '–ü–æ–∑–∏—Ç–∏–≤–Ω—ã–µ –≤–∏–±—Ä–∞—Ü–∏–∏': [587, 740, 932, 740, 587],
      '–ú–µ–ª–∞–Ω—Ö–æ–ª–∏—á–Ω–∞—è –±–∞–ª–ª–∞–¥–∞': [220, 247, 277, 247, 220],
      '–ì—Ä—É—Å—Ç–Ω–∞—è –º–µ–ª–æ–¥–∏—è': [196, 233, 262, 233, 196],
      '–ù–æ—Å—Ç–∞–ª—å–≥–∏—á–µ—Å–∫–∏–µ –Ω–æ—Ç—ã': [175, 208, 247, 208, 175],
      '–≠–Ω–µ—Ä–≥–∏—á–Ω—ã–π —Ä–æ–∫': [330, 415, 523, 415, 330],
      '–ú–æ—â–Ω—ã–π –±–∏—Ç': [294, 370, 466, 370, 294],
      '–ê–≥—Ä–µ—Å—Å–∏–≤–Ω—ã–µ —Ä–∏—Ñ—Ñ—ã': [277, 349, 440, 349, 277]
    };

    const notes = trackMelodies[currentTrack.track] || [440, 494, 523, 494, 440];

    notes.forEach((freq, i) => {
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();

      oscillator.frequency.setValueAtTime(freq, audioContext.currentTime + i * 0.6);
      oscillator.type = currentTrack.track.includes('—Ä–æ–∫') || currentTrack.track.includes('–±–∏—Ç') ? 'square' : 'sine';
      gainNode.gain.setValueAtTime(0.3, audioContext.currentTime + i * 0.6);
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + i * 0.6 + 0.5);

      oscillator.connect(gainNode);
      gainNode.connect(audioContext.destination);

      oscillator.start(audioContext.currentTime + i * 0.6);
      oscillator.stop(audioContext.currentTime + i * 0.6 + 0.5);
    });

    setDuration(180);
  };

  const togglePlayback = () => {
    if (!currentTrack) return;

    if (!isPlaying) {
      setIsPlaying(true);
      setCurrentTime(0);
      playRealAudio();

      intervalRef.current = setInterval(() => {
        setCurrentTime(prev => {
          if (prev >= 180) {
            setIsPlaying(false);
            clearInterval(intervalRef.current);
            return 0;
          }
          return prev + 1;
        });
      }, 1000);
    } else {
      setIsPlaying(false);
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
      setCurrentTime(0);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (!currentTrack) {
    return (
      <div className="player-container">
        <div style={{ textAlign: 'center', color: '#666' }}>
          –í—ã–±–µ—Ä–∏—Ç–µ —Ç—Ä–µ–∫ –¥–ª—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
        </div>
      </div>
    );
  }

  return (
    <div className="player-container">
      <h3 style={{ marginBottom: '15px' }}>–°–µ–π—á–∞—Å –∏–≥—Ä–∞–µ—Ç</h3>
      <div className="current-track">
        <div className="track-cover"></div>
        <div className="track-info">
          <div style={{ fontWeight: 'bold' }}>{currentTrack.track}</div>
          <div style={{ color: '#666', fontSize: '14px' }}>{currentTrack.playlist}</div>
        </div>
        <button className="play-btn" onClick={togglePlayback}>
          {isPlaying ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
        </button>
      </div>

      {/* –ü—Ä–æ–≥—Ä–µ—Å—Å –±–∞—Ä */}
      <div style={{ marginTop: '15px' }}>
        <input
          type="range"
          min="0"
          max={duration || 0}
          value={currentTime}
          onChange={(e) => {
            const newTime = parseInt(e.target.value);
            setCurrentTime(newTime);
          }}
          style={{
            width: '100%',
            height: '4px',
            background: '#ddd',
            borderRadius: '2px',
            marginBottom: '5px',
            cursor: 'pointer'
          }}
        />
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          fontSize: '12px', 
          color: '#666' 
        }}>
          <span>{formatTime(currentTime)}</span>
          <span>{formatTime(duration)}</span>
        </div>
      </div>

      {isPlaying && (
        <div style={{ 
          textAlign: 'center', 
          marginTop: '10px', 
          fontSize: '12px', 
          color: '#FF6B35' 
        }}>
          üéµ –ò–≥—Ä–∞–µ—Ç: {currentTrack.track}
        </div>
      )}
    </div>
  );
}

export default MusicPlayer;
