import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import MiniPlayer from '../components/MiniPlayer';

function DiaryPage() {
  const [entries, setEntries] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [viewMode, setViewMode] = useState('list');
  const [miniPlayerTrack, setMiniPlayerTrack] = useState(null);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const audioRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // –ó–∞–≥—Ä—É–∂–∞–µ–º –∑–∞–ø–∏—Å–∏ –∏–∑ localStorage –∏ –¥–æ–±–∞–≤–ª—è–µ–º –¥–µ–º–æ-–¥–∞–Ω–Ω—ã–µ
    const savedEntries = JSON.parse(localStorage.getItem('diaryEntries') || '[]');
    const demoEntries = [
      {
        id: 1,
        date: new Date(Date.now() - 86400000).toISOString(),
        moods: { happy: { intensity: 80 }, calm: { intensity: 60 } },
        music: { track: '–°–æ–ª–Ω–µ—á–Ω–∞—è –º–µ–ª–æ–¥–∏—è', playlist: '–•–æ—Ä–æ—à–µ–µ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ' },
        note: '–û—Ç–ª–∏—á–Ω—ã–π –¥–µ–Ω—å –Ω–∞ —Ä–∞–±–æ—Ç–µ!'
      },
      {
        id: 2,
        date: new Date(Date.now() - 172800000).toISOString(),
        moods: { sad: { intensity: 70 } },
        music: { track: '–ì—Ä—É—Å—Ç–Ω–∞—è –±–∞–ª–ª–∞–¥–∞', playlist: '–ú–µ–ª–∞–Ω—Ö–æ–ª–∏—è' },
        note: '–ù–µ–º–Ω–æ–≥–æ –≥—Ä—É—Å—Ç–Ω–æ –∏–∑-–∑–∞ –¥–æ–∂–¥—è'
      }
    ];

    const allEntries = [...savedEntries, ...demoEntries];
    setEntries(allEntries);
  }, []);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return '–°–µ–≥–æ–¥–Ω—è';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return '–í—á–µ—Ä–∞';
    } else {
      return date.toLocaleDateString('ru-RU', { 
        day: 'numeric', 
        month: 'long',
        year: 'numeric'
      });
    }
  };

  const getMoodEmojis = (moods) => {
    const moodMap = {
      happy: 'üòä',
      sad: 'üò¢',
      angry: 'üò†',
      calm: 'üòå',
      excited: 'ü§©',
      anxious: 'üò∞',
      love: 'ü•∞',
      tired: 'üò¥'
    };

    return Object.keys(moods).map(moodId => moodMap[moodId]).join(' ');
  };

  const getEntriesForDate = (date) => {
    const dateStr = date.toDateString();
    return entries.filter(entry => new Date(entry.date).toDateString() === dateStr);
  };

  const playTrack = (track) => {
    if (currentTrack === track && isPlaying) {
      setIsPlaying(false);
      if (audioRef.current) audioRef.current.pause();
      return;
    }

    setCurrentTrack(track);
    setIsPlaying(true);
    setCurrentTime(0);

    // –ü—Ä–æ—Å—Ç–∞—è —Å–∏–º—É–ª—è—Ü–∏—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
    const interval = setInterval(() => {
      setCurrentTime(prev => {
        if (prev >= 180) {
          setIsPlaying(false);
          clearInterval(interval);
          return 0;
        }
        return prev + 1;
      });
    }, 1000);

    audioRef.current = { interval };
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const renderCalendar = () => {
    const today = new Date();
    const currentMonth = today.getMonth();
    const currentYear = today.getFullYear();
    const firstDay = new Date(currentYear, currentMonth, 1);
    const startDate = new Date(firstDay);
    startDate.setDate(startDate.getDate() - firstDay.getDay());

    const days = [];
    for (let i = 0; i < 42; i++) {
      const date = new Date(startDate);
      date.setDate(startDate.getDate() + i);
      const dayEntries = getEntriesForDate(date);

      days.push(
        <div
          key={i}
          onClick={() => setSelectedDate(date)}
          style={{
            padding: '8px',
            border: '1px solid #ddd',
            cursor: 'pointer',
            backgroundColor: dayEntries.length > 0 ? '#0ABAB5' : 'white',
            color: dayEntries.length > 0 ? 'white' : '#333',
            opacity: date.getMonth() !== currentMonth ? 0.3 : 1,
            textAlign: 'center'
          }}
        >
          <div>{date.getDate()}</div>
          {dayEntries.length > 0 && <div style={{ fontSize: '10px' }}>üìù</div>}
        </div>
      );
    }

    return (
      <div>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)', gap: '2px', marginBottom: '20px' }}>
          {['–í—Å', '–ü–Ω', '–í—Ç', '–°—Ä', '–ß—Ç', '–ü—Ç', '–°–±'].map(day => (
            <div key={day} style={{ padding: '10px', fontWeight: 'bold', textAlign: 'center', color: 'white' }}>
              {day}
            </div>
          ))}
          {days}
        </div>

        {selectedDate && (
          <div className="card">
            <h3>{selectedDate.toLocaleDateString('ru-RU')}</h3>
            {getEntriesForDate(selectedDate).length > 0 ? (
              <>
                {getEntriesForDate(selectedDate).map(entry => (
                  <div key={entry.id} style={{ marginBottom: '15px', padding: '10px', backgroundColor: '#f8f8f8', borderRadius: '8px' }}>
                    <div style={{ marginBottom: '10px' }}>
                      <span style={{ fontSize: '20px' }}>{getMoodEmojis(entry.moods)}</span>
                    </div>
                    {entry.music && (
                      <div 
                        className="calendar-music-item"
                        onClick={() => playTrack(entry.music.track)}
                      >
                        <span>{currentTrack === entry.music.track && isPlaying ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}</span>
                        <span>üéµ {entry.music.track}</span>
                      </div>
                    )}
                    {entry.note && (
                      <div style={{ fontSize: '14px', fontStyle: 'italic' }}>{entry.note}</div>
                    )}
                  </div>
                ))}

                {/* –í—Å—Ç—Ä–æ–µ–Ω–Ω—ã–π –ø–ª–µ–µ—Ä –¥–ª—è –∫–∞–ª–µ–Ω–¥–∞—Ä—è */}
                {currentTrack && (
                  <div className="calendar-player">
                    <div className="calendar-player-controls">
                      <button
                        className="calendar-play-btn"
                        onClick={() => playTrack(currentTrack)}
                      >
                        {isPlaying ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
                      </button>
                      <div className="calendar-track-info">
                        <div className="calendar-track-title">{currentTrack}</div>
                        <div className="calendar-track-subtitle">–ò–∑ –¥–Ω–µ–≤–Ω–∏–∫–∞</div>
                      </div>
                    </div>

                    <div className="calendar-progress-bar">
                      <div 
                        className="calendar-progress-fill"
                        style={{ width: `${(currentTime / 180) * 100}%` }}
                      />
                    </div>

                    <div className="calendar-time-display">
                      <span>{formatTime(currentTime)}</span>
                      <span>{formatTime(180)}</span>
                    </div>

                    {isPlaying && (
                      <div className="calendar-playing-indicator">
                        üéµ –ò–≥—Ä–∞–µ—Ç –≤ –∫–∞–ª–µ–Ω–¥–∞—Ä–µ
                      </div>
                    )}
                  </div>
                )}
              </>
            ) : (
              <p>–ù–µ—Ç –∑–∞–ø–∏—Å–µ–π –∑–∞ —ç—Ç–æ—Ç –¥–µ–Ω—å</p>
            )}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="container">
      <div className="header">
        <h2 style={{ color: 'white', marginBottom: '20px' }}>–í–∞—à –¥–Ω–µ–≤–Ω–∏–∫ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–π</h2>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <button 
          className={viewMode === 'list' ? 'btn btn-primary' : 'btn btn-secondary'}
          onClick={() => setViewMode('list')}
          style={{ marginRight: '10px' }}
        >
          –°–ø–∏—Å–æ–∫
        </button>
        <button 
          className={viewMode === 'calendar' ? 'btn btn-primary' : 'btn btn-secondary'}
          onClick={() => setViewMode('calendar')}
        >
          –ö–∞–ª–µ–Ω–¥–∞—Ä—å
        </button>
      </div>

      {viewMode === 'calendar' ? renderCalendar() : (
        entries.length === 0 ? (
        <div className="empty-state">
          <div style={{ fontSize: '3rem', marginBottom: '20px' }}>üéµ</div>
          <h3>–ü–æ–∫–∞ –Ω–µ—Ç –∑–∞–ø–∏—Å–µ–π</h3>
          <p>–ù–∞—á–Ω–∏—Ç–µ –æ—Ç—Å–ª–µ–∂–∏–≤–∞—Ç—å —Å–≤–æ–∏ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏—è!</p>
          <button 
            className="btn btn-primary" 
            onClick={() => navigate('/mood')}
            style={{ marginTop: '20px' }}
          >
            –î–æ–±–∞–≤–∏—Ç—å –∑–∞–ø–∏—Å—å
          </button>
        </div>
      ) : (
        <>
          {entries.map(entry => (
            <div key={entry.id} className="diary-entry">
              <div className="entry-header">
                <div style={{ fontWeight: 'bold' }}>{formatDate(entry.date)}</div>
                <div style={{ color: '#666', fontSize: '14px' }}>
                  {new Date(entry.date).toLocaleTimeString('ru-RU', { 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  })}
                </div>
              </div>

              <div style={{ marginBottom: '15px' }}>
                <div style={{ fontSize: '14px', fontWeight: 'bold', color: '#666', marginBottom: '8px' }}>
                  –ù–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ:
                </div>
                <div className="moods-display">
                  <div className="mood-emojis">{getMoodEmojis(entry.moods)}</div>
                  <div>
                    {Object.entries(entry.moods).map(([moodId, data]) => (
                      <div key={moodId} style={{ fontSize: '12px', color: '#888' }}>
                        {moodId}: {Math.round(data.intensity)}%
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              {entry.music && (
                <div style={{ marginBottom: '15px' }}>
                  <div style={{ fontSize: '14px', fontWeight: 'bold', color: '#666', marginBottom: '8px' }}>
                    –ú—É–∑—ã–∫–∞:
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center' }}>
                    <span style={{ marginRight: '10px' }}>üéµ</span>
                    <div>
                      <div style={{ fontWeight: 'bold' }}>{entry.music.track}</div>
                      <div style={{ fontSize: '12px', color: '#666' }}>{entry.music.playlist}</div>
                    </div>
                  </div>
                </div>
              )}

              {entry.note && (
                <div>
                  <div style={{ fontSize: '14px', fontWeight: 'bold', color: '#666', marginBottom: '8px' }}>
                    –ó–∞–º–µ—Ç–∫–∞:
                  </div>
                  <div className="note-text">{entry.note}</div>
                </div>
              )}
            </div>
          ))}

          <button 
            className="btn btn-primary" 
            onClick={() => navigate('/mood')}
          >
            –î–æ–±–∞–≤–∏—Ç—å –Ω–æ–≤—É—é –∑–∞–ø–∏—Å—å
          </button>
        </>
        )
      )}

      <MiniPlayer 
        track={miniPlayerTrack} 
        onClose={() => setMiniPlayerTrack(null)} 
      />
    </div>
  );
}

export default DiaryPage;
