  '–¢—Ä–µ–∫ 1': 'https://www.soundjay.com/misc/sounds/bell-ringing-05.wav',
  '–¢—Ä–µ–∫ 2': 'https://www.soundjay.com/misc/sounds/bell-ringing-05.wav',
  '–¢—Ä–µ–∫ 3': 'https://www.soundjay.com/misc/sounds/bell-ringing-05.wav',
  '–ü–æ–ø—É–ª—è—Ä–Ω—ã–π —Ç—Ä–µ–∫': 'https://www.soundjay.com/misc/sounds/bell-ringing-05.wav',
  '–•–∏—Ç –¥–Ω—è': 'https://www.soundjay.com/misc/sounds/bell-ringing-05.wav',
  '–ù–æ–≤–∏–Ω–∫–∞': 'https://www.soundjay.com/misc/sounds/bell-ringing-05.wav',
  '–õ—é–±–∏–º–∞—è –ø–µ—Å–Ω—è': 'https://www.soundjay.com/misc/sounds/bell-ringing-05.wav',
  '–†–µ—Ç—Ä–æ —Ö–∏—Ç': 'https://www.soundjay.com/misc/sounds/bell-ringing-05.wav',
  '–ò–Ω–¥–∏ —Ç—Ä–µ–∫': 'https://www.soundjay.com/misc/sounds/bell-ringing-05.wav'
};

function MusicPlayer({ currentTrack, onTrackChange }) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const audioRef = useRef(null);

  useEffect(() => {
    if (currentTrack && audioRef.current) {
      // –°–æ–∑–¥–∞–µ–º –ø—Ä–æ—Å—Ç–æ–π —Å–∏–Ω—Ç–µ–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –∑–≤—É–∫ –¥–ª—è –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü–∏–∏
      createDemoAudio();
    }
  }, [currentTrack]);

  const createDemoAudio = () => {
    // –°–æ–∑–¥–∞–µ–º AudioContext –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –∑–≤—É–∫–∞
    const audioContext = new (window.AudioContext || window.webkitAudioContext)();
    const oscillator = audioContext.createOscillator();
    const gainNode = audioContext.createGain();

    // –ù–∞—Å—Ç—Ä–∞–∏–≤–∞–µ–º –∑–≤—É–∫ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ç—Ä–µ–∫–∞
    const frequencies = {
      '–¢—Ä–µ–∫ 1': 440, // A4
      '–¢—Ä–µ–∫ 2': 523, // C5
      '–¢—Ä–µ–∫ 3': 659, // E5
      '–ü–æ–ø—É–ª—è—Ä–Ω—ã–π —Ç—Ä–µ–∫': 392, // G4
      '–•–∏—Ç –¥–Ω—è': 494, // B4
      '–ù–æ–≤–∏–Ω–∫–∞': 587, // D5
      '–õ—é–±–∏–º–∞—è –ø–µ—Å–Ω—è': 349, // F4
      '–†–µ—Ç—Ä–æ —Ö–∏—Ç': 330, // E4
      '–ò–Ω–¥–∏ —Ç—Ä–µ–∫': 294  // D4
    };

    oscillator.frequency.setValueAtTime(frequencies[currentTrack.track] || 440, audioContext.currentTime);
    oscillator.type = 'sine';

    gainNode.gain.setValueAtTime(0.1, audioContext.currentTime);

    oscillator.connect(gainNode);
    gainNode.connect(audioContext.destination);

    if (isPlaying) {
      oscillator.start();
      setTimeout(() => {
        oscillator.stop();
      }, 3000); // –ò–≥—Ä–∞–µ–º 3 —Å–µ–∫—É–Ω–¥—ã
    }
  };

  const togglePlayback = () => {
    if (!currentTrack) return;

    setIsPlaying(!isPlaying);

    if (!isPlaying) {
      createDemoAudio();
      // –ò–º–∏—Ç–∏—Ä—É–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
      const interval = setInterval(() => {
        setCurrentTime(prev => {
          if (prev >= 30) { // 30 —Å–µ–∫—É–Ω–¥ –¥–µ–º–æ
            setIsPlaying(false);
            clearInterval(interval);
            return 0;
          }
          return prev + 1;
        });
      }, 1000);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (!currentTrack) {
    return (
      <div className="player-container">
        <div style={{ textAlign: 'center', color: '#666' }}>
          –í—ã–±–µ—Ä–∏—Ç–µ —Ç—Ä–µ–∫ –¥–ª—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
        </div>
      </div>
    );
  }

  return (
    <div className="player-container">
      <h3 style={{ marginBottom: '15px' }}>–°–µ–π—á–∞—Å –∏–≥—Ä–∞–µ—Ç</h3>
      <div className="current-track">
        <div className="track-cover"></div>
        <div className="track-info">
          <div style={{ fontWeight: 'bold' }}>{currentTrack.track}</div>
          <div style={{ color: '#666', fontSize: '14px' }}>{currentTrack.playlist}</div>
        </div>
        <button className="play-btn" onClick={togglePlayback}>
          {isPlaying ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
        </button>
      </div>

      {/* –ü—Ä–æ–≥—Ä–µ—Å—Å –±–∞—Ä */}
      <div style={{ marginTop: '15px' }}>
        <div style={{ 
          width: '100%', 
          height: '4px', 
          background: '#ddd', 
          borderRadius: '2px',
          marginBottom: '5px'
        }}>
          <div style={{
            width: `${(currentTime / 30) * 100}%`,
            height: '100%',
            background: '#FF6B35',
            borderRadius: '2px',
            transition: 'width 0.3s'
          }}></div>
        </div>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          fontSize: '12px', 
          color: '#666' 
        }}>
          <span>{formatTime(currentTime)}</span>
          <span>0:30</span>
        </div>
      </div>

      {isPlaying && (
        <div style={{ 
          textAlign: 'center', 
          marginTop: '10px', 
          fontSize: '12px', 
          color: '#FF6B35' 
        }}>
          üéµ –í–æ—Å–ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç—Å—è –¥–µ–º–æ-–≤–µ—Ä—Å–∏—è
        </div>
      )}
    </div>
  );
}

export default MusicPlayer;
