import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { usePlayer } from '../contexts/PlayerContext';

function MusicPlayerPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const { moods } = location.state || { moods: { happy: { intensity: 70 } } }; // Дефолтное настроение
  const [playlists, setPlaylists] = useState([]);
  const { playTrack: playGlobalTrack, currentTrack } = usePlayer();
  const [note, setNote] = useState('');

  useEffect(() => {
    const moodTracks = {
      happy: ['Солнечная мелодия', 'Радостный ритм', 'Позитивные вибрации'],
      sad: ['Меланхоличная баллада', 'Грустная мелодия', 'Ностальгические ноты'],
      angry: ['Энергичный рок', 'Мощный бит', 'Агрессивные риффы'],
      calm: ['Расслабляющая музыка', 'Медитативная композиция', 'Умиротворяющие звуки'],
      excited: ['Драйвовый трек', 'Танцевальный хит', 'Электронные биты'],
      anxious: ['Успокаивающая музыка', 'Мягкая мелодия', 'Антистресс трек'],
      love: ['Романтическая мелодия', 'Любовная баллада', 'Нежные ноты'],
      tired: ['Спокойная композиция', 'Расслабляющие звуки', 'Медленная мелодия']
    };

    const selectedMoods = Object.keys(moods);
    const mainMood = selectedMoods[0] || 'happy';

    const mockPlaylists = [
      {
        id: 1,
        title: `Музыка для ${mainMood}`,
        tracks: moodTracks[mainMood] || moodTracks.happy
      }
    ];
    setPlaylists(mockPlaylists);
  }, [moods]);

  const playTrack = (playlist, trackIndex) => {
    playGlobalTrack(playlist.tracks[trackIndex]);
  };

  const saveToDiary = () => {
    if (!currentTrack) {
      alert('Выберите трек для сохранения');
      return;
    }

    const entry = {
      date: new Date().toISOString(),
      moods,
      music: { track: currentTrack, playlist: 'Выбранная музыка' },
      note
    };

    // Сохраняем в localStorage для демонстрации
    const entries = JSON.parse(localStorage.getItem('diaryEntries') || '[]');
    entries.unshift({ id: Date.now(), ...entry });
    localStorage.setItem('diaryEntries', JSON.stringify(entries));

    alert('Запись сохранена в дневник!');
    navigate('/diary');
  };

  return (
    <div className="container">
      <div className="header">
        <h2 style={{ color: 'white', marginBottom: '20px' }}>Музыка для вашего настроения</h2>
      </div>

      {playlists.map(playlist => (
        <div key={playlist.id} className="playlist-card">
          <div className="playlist-cover">{playlist.id}</div>
          <h3 style={{ textAlign: 'center', marginBottom: '15px' }}>{playlist.title}</h3>
          <div>
            {playlist.tracks.map((track, index) => (
              <div
                key={index}
                className="track-item"
                onClick={() => playGlobalTrack(track)}
              >
                <span style={{ marginRight: '10px' }}>▶️</span>
                <span>{track}</span>
              </div>
            ))}
          </div>
        </div>
      ))}



      <div className="card">
        <h3 style={{ marginBottom: '10px' }}>Добавить заметку:</h3>
        <textarea
          className="input"
          placeholder="Что вы чувствуете сейчас?"
          value={note}
          onChange={(e) => setNote(e.target.value)}
          rows="3"
          style={{ resize: 'vertical', minHeight: '80px' }}
        />
      </div>

      <button className="btn btn-primary" onClick={saveToDiary}>
        Сохранить в дневник
      </button>
    </div>
  );
}

export default MusicPlayerPage;
