import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ApiService from '../services/api';

function AdminPage() {
  const [diaryEntries, setDiaryEntries] = useState([]);
  const [likedTracks, setLikedTracks] = useState([]);
  const [activeTab, setActiveTab] = useState('diary');
  const navigate = useNavigate();

  useEffect(() => {
    const userRole = localStorage.getItem('userRole');
    if (userRole !== 'admin') {
      navigate('/mood');
      return;
    }
    loadData();
  }, [navigate]);

  const loadData = async () => {
    try {
      const entries = await ApiService.getDiaryEntries();
      const tracks = await ApiService.getLikedTracks();
      setDiaryEntries(entries);
      setLikedTracks(tracks);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö:', error);
    }
  };

  return (
    <div className="container">
      <div className="header">
        <h2 style={{ color: 'white', marginBottom: '20px' }}>üîß –ê–¥–º–∏–Ω-–ø–∞–Ω–µ–ª—å</h2>
      </div>

      <div style={{ 
        marginBottom: '30px', 
        display: 'flex', 
        justifyContent: 'center', 
        gap: '15px',
        flexWrap: 'wrap'
      }}>
        <button 
          className={activeTab === 'diary' ? 'btn btn-primary' : 'btn btn-secondary'}
          onClick={() => setActiveTab('diary')}
          style={{ minWidth: '120px', width: 'auto' }}
        >
          üìù –ó–∞–ø–∏—Å–∏ –¥–Ω–µ–≤–Ω–∏–∫–∞
        </button>
        <button 
          className={activeTab === 'liked' ? 'btn btn-primary' : 'btn btn-secondary'}
          onClick={() => setActiveTab('liked')}
          style={{ minWidth: '120px', width: 'auto' }}
        >
          ‚ù§Ô∏è –õ–∞–π–∫–∏
        </button>
      </div>

      {activeTab === 'diary' && (
        <div className="card">
          <h3>–ó–∞–ø–∏—Å–∏ –¥–Ω–µ–≤–Ω–∏–∫–∞ ({diaryEntries.length})</h3>
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
              <thead>
                <tr style={{ borderBottom: '2px solid #ddd' }}>
                  <th style={{ padding: '10px', textAlign: 'left' }}>ID</th>
                  <th style={{ padding: '10px', textAlign: 'left' }}>–î–∞—Ç–∞</th>
                  <th style={{ padding: '10px', textAlign: 'left' }}>–ù–∞—Å—Ç—Ä–æ–µ–Ω–∏—è</th>
                  <th style={{ padding: '10px', textAlign: 'left' }}>–ú—É–∑—ã–∫–∞</th>
                  <th style={{ padding: '10px', textAlign: 'left' }}>–ó–∞–º–µ—Ç–∫–∞</th>
                </tr>
              </thead>
              <tbody>
                {diaryEntries.map(entry => (
                  <tr key={entry.id} style={{ borderBottom: '1px solid #eee' }}>
                    <td style={{ padding: '10px' }}>{entry.id}</td>
                    <td style={{ padding: '10px' }}>
                      {new Date(entry.date).toLocaleString('ru-RU')}
                    </td>
                    <td style={{ padding: '10px' }}>
                      {Object.keys(entry.moods).join(', ')}
                    </td>
                    <td style={{ padding: '10px' }}>
                      {entry.music?.track || '-'}
                    </td>
                    <td style={{ padding: '10px', maxWidth: '200px' }}>
                      {entry.note || '-'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {activeTab === 'liked' && (
        <div className="card">
          <h3>–ü–æ–Ω—Ä–∞–≤–∏–≤—à–∏–µ—Å—è —Ç—Ä–µ–∫–∏ ({likedTracks.length})</h3>
          <div style={{ marginTop: '20px' }}>
            {likedTracks.length === 0 ? (
              <p style={{ textAlign: 'center', color: '#666' }}>–ù–µ—Ç –ø–æ–Ω—Ä–∞–≤–∏–≤—à–∏—Ö—Å—è —Ç—Ä–µ–∫–æ–≤</p>
            ) : (
              <ul style={{ listStyle: 'none', padding: 0 }}>
                {likedTracks.map((track, index) => (
                  <li key={index} style={{ 
                    padding: '10px', 
                    borderBottom: '1px solid #eee',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '10px'
                  }}>
                    <span style={{ fontSize: '20px' }}>‚ù§Ô∏è</span>
                    <span>{track}</span>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      )}

      <div className="card" style={{ marginTop: '20px', backgroundColor: '#f8f8f8' }}>
        <h4>üìä –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px', marginTop: '15px' }}>
          <div style={{ padding: '15px', backgroundColor: 'white', borderRadius: '8px' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#0ABAB5' }}>
              {diaryEntries.length}
            </div>
            <div style={{ fontSize: '14px', color: '#666' }}>–í—Å–µ–≥–æ –∑–∞–ø–∏—Å–µ–π</div>
          </div>
          <div style={{ padding: '15px', backgroundColor: 'white', borderRadius: '8px' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#ff4757' }}>
              {likedTracks.length}
            </div>
            <div style={{ fontSize: '14px', color: '#666' }}>–ü–æ–Ω—Ä–∞–≤–∏–≤—à–∏—Ö—Å—è —Ç—Ä–µ–∫–æ–≤</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminPage;
