import React from 'react';
import { usePlayer } from '../contexts/PlayerContext';

function GlobalPlayer() {
  const { currentTrack, isPlaying, currentTime, playTrack, stopPlayer, formatTime } = usePlayer();

  if (!currentTrack) return null;

  return (
    <div style={{
      position: 'fixed',
      bottom: '20px',
      left: '50%',
      transform: 'translateX(-50%)',
      background: 'white',
      borderRadius: '15px',
      padding: '15px',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      zIndex: 1001,
      maxWidth: '400px',
      width: '90%'
    }}>
      <button 
        onClick={() => playTrack(currentTrack)}
        style={{
          background: '#0ABAB5',
          border: 'none',
          borderRadius: '50%',
          width: '40px',
          height: '40px',
          color: 'white',
          fontSize: '16px',
          cursor: 'pointer'
        }}
      >
        {isPlaying ? '⏸️' : '▶️'}
      </button>

      <div style={{ flex: 1 }}>
        <div style={{ fontWeight: 'bold', fontSize: '14px' }}>{currentTrack}</div>
        <div style={{ fontSize: '12px', color: '#666' }}>
          {formatTime(currentTime)} / {formatTime(180)}
        </div>
        <div style={{
          width: '100%',
          height: '3px',
          backgroundColor: '#ddd',
          borderRadius: '2px',
          marginTop: '5px',
          overflow: 'hidden'
        }}>
          <div style={{
            width: `${(currentTime / 180) * 100}%`,
            height: '100%',
            backgroundColor: '#0ABAB5',
            transition: 'width 0.3s ease'
          }} />
        </div>
      </div>

      <button 
        onClick={stopPlayer}
        style={{
          background: 'none',
          border: 'none',
          fontSize: '18px',
          cursor: 'pointer',
          color: '#999'
        }}
      >
        ✕
      </button>
    </div>
  );
}

export default GlobalPlayer;
