import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import MusicPlayer from '../components/MusicPlayer';

function MusicPlayerPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const { moods } = location.state || { moods: { happy: { intensity: 70 } } }; // Дефолтное настроение
  const [playlists, setPlaylists] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [note, setNote] = useState('');

  useEffect(() => {
    const moodNames = Object.keys(moods);
    const allTracks = [
      'Энергичная мелодия', 'Спокойная композиция', 'Ритмичный бит',
      'Мечтательная песня', 'Драйвовый трек', 'Нежная баллада',
      'Танцевальный хит', 'Расслабляющая музыка', 'Вдохновляющий саунд',
      'Романтическая мелодия', 'Бодрящий ритм', 'Медитативная композиция'
    ];

    const mockPlaylists = [
      {
        id: 1,
        title: `Музыка для ${moodNames[0] || 'настроения'}`,
        tracks: ['Энергичная мелодия', 'Спокойная композиция', 'Ритмичный бит']
      },
      {
        id: 2,
        title: 'Персональная подборка',
        tracks: ['Мечтательная песня', 'Драйвовый трек', 'Нежная баллада']
      }
    ];
    setPlaylists(mockPlaylists);
  }, [moods]);

  const playTrack = (playlist, trackIndex) => {
    setCurrentTrack({
      playlist: playlist.title,
      track: playlist.tracks[trackIndex]
    });
  };

  const saveToDiary = () => {
    if (!currentTrack) {
      alert('Выберите трек для сохранения');
      return;
    }

    const entry = {
      date: new Date().toISOString(),
      moods,
      music: currentTrack,
      note
    };

    // Сохраняем в localStorage для демонстрации
    const entries = JSON.parse(localStorage.getItem('diaryEntries') || '[]');
    entries.unshift({ id: Date.now(), ...entry });
    localStorage.setItem('diaryEntries', JSON.stringify(entries));

    alert('Запись сохранена в дневник!');
    navigate('/diary');
  };

  return (
    <div className="container">
      <div className="header">
        <h2 style={{ color: 'white', marginBottom: '20px' }}>Музыка для вашего настроения</h2>
      </div>

      {playlists.map(playlist => (
        <div key={playlist.id} className="playlist-card">
          <div className="playlist-cover">{playlist.id}</div>
          <h3 style={{ textAlign: 'center', marginBottom: '15px' }}>{playlist.title}</h3>
          <div>
            {playlist.tracks.map((track, index) => (
              <div
                key={index}
                className="track-item"
                onClick={() => playTrack(playlist, index)}
              >
                <span style={{ marginRight: '10px' }}>▶️</span>
                <span>{track}</span>
              </div>
            ))}
          </div>
        </div>
      ))}

      <MusicPlayer currentTrack={currentTrack} onTrackChange={setCurrentTrack} />

      <div className="card">
        <h3 style={{ marginBottom: '10px' }}>Добавить заметку:</h3>
        <textarea
          className="input"
          placeholder="Что вы чувствуете сейчас?"
          value={note}
          onChange={(e) => setNote(e.target.value)}
          rows="3"
          style={{ resize: 'vertical', minHeight: '80px' }}
        />
      </div>

      <button className="btn btn-primary" onClick={saveToDiary}>
        Сохранить в дневник
      </button>
    </div>
  );
}

export default MusicPlayerPage;
